import React, { useState, useEffect } from "react";
import { Form, Input, Button, DatePicker } from "antd";
import { useParams } from "react-router-dom";
import moment from "moment";

// Instruments
import { api } from "../../api";

import { routes } from "../../navigation/routes";
import { history } from "../../navigation/history";

//Styles
import style from "../../pages/style";
const { layout, tailLayout } = style;

export const TodoItem = ({ token, todos }) => {
  const { id: todoId } = useParams();
  const [todo, setTodo] = useState({});

  const onFinish = async values => {
    try {
      await api.updateTodo(todoId, token, values);
      history.push(routes.todo);
    } catch ({ message }) {
      console.error(message);
    }
  };

  const onFinishFailed = errorInfo => {
    console.log("Failed:", errorInfo);
  };

  useEffect(() => {
    const loadTodos = async () => {
      try {
        const data = await api.readTodos(token);
        const sourceTodo = data.find(({ id }) => id === todoId);
        console.log("---sourceTodo", sourceTodo);

        setTodo({ ...sourceTodo, dd: moment(sourceTodo.dd) });
      } catch ({ message }) {
        console.log(message);
      }
    };

    if (!token) {
      history.push(routes.signIn);
    } else {
      loadTodos();
    }
  }, [todoId, token]);

  return todo.id ? (
    <Row style={{ marginTop: "35vh" }}>
      <Col span={12} offset={6}>
        <Card title="Log in">
          <Form
            {...layout}
            name="basic"
            initialValues={todo}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
          >
            <Form.Item
              label="Name"
              name="name"
              rules={[
                {
                  required: true,
                  message: "Please input your name!"
                }
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Description"
              name="description"
              rules={[
                {
                  required: true,
                  message: "Please input your description!"
                }
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Status"
              name="status"
              rules={[
                {
                  required: true,
                  message: "Please input your description!"
                }
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Due date"
              name="dd"
              rules={[
                {
                  required: true,
                  message: "Please input your due date!"
                }
              ]}
            >
              <DatePicker />
            </Form.Item>
            <Form.Item {...tailLayout}>
              <Button type="primary" htmlType="submit">
                Update Todo
              </Button>
            </Form.Item>
          </Form>
        </Card>
      </Col>
    </Row>
  ) : null;
};
