import React, { useState, useEffect } from "react";
import {
  List,
  Form,
  Input,
  Button,
  DatePicker,
  Row,
  Col,
  Card,
  Checkbox,
  Modal
} from "antd";
import { TodoItem } from "../TodoItem";

// Instruments
import { api } from "../../api";
import { Link } from "react-router-dom";

import { routes } from "../../navigation/routes";
import { history } from "../../navigation/history";

//Styles
import style from "../../pages/style";
const { layout, tailLayout } = style;

export const Todos = ({ token }) => {
  const [todos, setTodos] = useState([]);
  const [edit, setEdit] = useState(false);
  const [id, setId] = useState(false);

  const onFinish = async values => {
    try {
      const todo = await api.createTodo(token, values);
      setTodos(todos => [todo, ...todos]);
    } catch ({ message }) {
      console.error(message);
    }
  };

  const onFinishFailed = errorInfo => {
    console.log("Failed:", errorInfo);
  };

  useEffect(() => {
    const loadTodos = async () => {
      try {
        const data = await api.readTodos(token);
        setTodos(data);
        console.log("---data", data);
      } catch ({ message }) {
        console.log(message);
      }
    };

    if (!token) {
      history.push(routes.signIn);
    } else {
      loadTodos();
    }
  }, [token]);
  console.log(id, token);
  return (
    <>
      <Modal
        // title="Order Details"
        width={"65vw"}
        visible={edit}
        onOk={() => {}}
        onCancel={() => {}}
      >
        <TodoItem token={token} idProps={id}></TodoItem>
      </Modal>
      <Row>
        <Col style={{ marginTop: "15px" }} span={4} offset={20}>
          <a
            onClick={() => {
              history.push(routes.signIn);
            }}
          >
            Switch Login
          </a>
        </Col>
      </Row>
      <Row style={{ marginTop: "15px" }}>
        <Col span={18} offset={3}>
          <Card title="Todo List">
            <Form
              {...layout}
              name="basic"
              initialValues={{
                remember: true
              }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
            >
              <Form.Item
                label="Name"
                name="name"
                rules={[
                  {
                    required: true,
                    message: "Please input your name!"
                  }
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Description"
                name="description"
                rules={[
                  {
                    required: true,
                    message: "Please input your description!"
                  }
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Due date"
                name="dd"
                rules={[
                  {
                    required: true,
                    message: "Please input your due date!"
                  }
                ]}
              >
                <DatePicker />
              </Form.Item>
              <Form.Item {...tailLayout}>
                <Button type="primary" htmlType="submit">
                  Create Todo
                </Button>
              </Form.Item>
            </Form>
            <List
              itemLayout="horizontal"
              dataSource={todos}
              renderItem={({ id, name, description, dd, status }) => {
                setId(id);
                return (
                  <List.Item
                    onClick={() => {
                      setEdit(!edit);
                    }}
                  >
                    <Checkbox
                      style={{ marginRight: "5px" }}
                      onChange={() => {}}
                    />
                    {status}
                    <List.Item.Meta
                      title={<Link to={`/${id}`}>{name}</Link>}
                      description={description}
                    />
                    <List.Item.Meta description={dd} />

                    {/* <List.Item.Meta description={status} /> */}
                  </List.Item>
                );
              }}
            />
          </Card>
        </Col>
      </Row>
    </>
  );
};
